import requestsimport json# Define a function to fetch fuel stations for a given location and write to a JSON filedef fetch_fuel_stations(location, filename):    # Set up the API endpoint and parameters    url = "https://maps.googleapis.com/maps/api/place/textsearch/json"    params = {        "query": f"fuel stations in {location}, Ireland",        "type": "gas_station",        "key": ""    }    # Make the API call    response = requests.get(url, params=params)    # Check if the request was successful    if response.status_code == 200:        # Parse the JSON response        data = response.json()        # Extract the relevant data from each result        fuel_stations = []        for result in data["results"]:            name = result["name"]            address = result["formatted_address"]            latitude = result["geometry"]["location"]["lat"]            longitude = result["geometry"]["location"]["lng"]            rating = result.get("rating", "N/A")            fuel_stations.append({"name": name, "address": address, "latitude": latitude, "longitude": longitude, "rating": rating})        # Check if there are more results        while "next_page_token" in data:            # Wait for a few seconds before making the next API call            import time            time.sleep(3)            # Set up the API endpoint and parameters for the next page of results            params["pagetoken"] = data["next_page_token"]            # Make the API call for the next page of results            response = requests.get(url, params=params)            # Parse the JSON response            data = response.json()            # Extract the relevant data from each result and add it to the fuel_stations list            for result in data["results"]:                name = result["name"]                address = result["formatted_address"]                latitude = result["geometry"]["location"]["lat"]                longitude = result["geometry"]["location"]["lng"]                rating = result.get("rating", "N/A")                fuel_stations.append({"name": name, "address": address, "latitude": latitude, "longitude": longitude, "rating": rating})        # Write the results to a file        with open(filename, "a") as outfile:            json.dump(fuel_stations, outfile)        print(f"Data written to {filename}")    else:        print("Error: API request failed")# Define a list of counties in Ireland (including Northern Ireland) in alphabetical ordercounties = ["Antrim", "Armagh", "Carlow", "Cavan", "Clare", "Cork", "Derry", "Donegal", "Down", "Dublin", "Fermanagh", "Galway", "Kerry", "Kildare", "Kilkenny", "Laois", "Leitrim", "Limerick", "Longford", "Louth", "Mayo", "Meath", "Monaghan", "Offaly", "Roscommon", "Sligo", "Tipperary", "Tyrone", "Waterford", "Westmeath", "Wexford", "Wicklow"]# Loop through the counties and fetch fuel stations for each countyfor county in counties:    filename = f"{county}_fuel_stations.json"    fetch_fuel_stations(county, filename)