import jsonimport randomwith open('combinedFormattedStations.json', 'r', encoding='utf-8') as f:    data = json.load(f)# Assign random prices to each fuel stationfor station in data:    station['petrolPrice'] = round(random.uniform(1.0, 2.1), 2)    station['dieselPrice'] = round(random.uniform(station['petrolPrice'], 2.2), 2)# Write the updated data back to the JSON filewith open('combinedFormattedStations.json', 'w') as f:    json.dump(data, f, indent=4)